section "game/modules/tome/data/chats/arena-start.lua"

t("None", "無", "_t")
t("Gladiator's wares", "角鬥士的裝備店", "_t")
t([[#LIGHT_GREEN#You take a look at the ranking boards before entering.
%s]], [[#LIGHT_GREEN#在進入之前，你看了一下排行榜。
%s]], "tformat")
t("Enter the arena (60 waves)", "競技場（60波）", "_t")
t("Enter the arena (shorter mode, 30 rounds)", "競技場（30波）", "_t")
t("#LIGHT_RED#[Reset all arena save data]", "#LIGHT_RED#[重置競技場數據]", "_t")
t("Enter the arena for 60 rounds", "進入60波競技場", "_t")
t("Enter the arena for 30 rounds", "進入30波競技場", "_t")


