section "game/modules/tome/data/talents/chronomancy/anomalies.lua"

t("Anomaly Rearrange", "異常：重排", "talent name")
t("@Source@ causes a spacetime hiccup.", "@Source@引起了時空輕微重排。", "_t")
t("Teleports up to five targets in a radius of %d up to ten tiles away.", "將半徑 %d 範圍內最多五個單位傳送到十格外。", "tformat")
t("Anomaly Teleport", "異常：傳送", "talent name")
t("@Source@ shifts reality.", "@Source@改變了現實。", "_t")
t("Teleports up to five targets in a radius of %d up to %d tiles away.", "將半徑 %d 範圍內最多五個單位傳送到 %d 格外。", "tformat")
t("Anomaly Swap", "異常：換位", "talent name")
