t("Trap Priming", "即爆陷阱", "talent name")
t("#LIGHT_BLUE#Cancelled Trap Priming.", "#LIGHT_BLUE#取消即爆陷阱", "logPlayer")
t("#YELLOW_GREEN#Dismantling %s (instant trigger)", "#YELLOW_GREEN#放置 %s中(立刻觸發)", "logPlayer")
t("#LIGHT_GREEN#Preparing %s (instant trigger)", "#LIGHT_GREEN#準備%s中(立刻觸發)", "logPlayer")
